sql.where:
u.id=i.user_id

    and i.status = #{status}


    and (u.username like #{search} or u.realname like #{search} or u.code like #{search})

mapper.select:
SELECT dor1.*,doc.user_id,doil1.pub_counts,doil1.finish_counts FROM (SELECT dor.online_id,dor.post_id,dor.candidate_id,sum(if(dor.ins_option=1, 1, 0)) AS option_sum FROM dr_online_result dor
and dor.online_id = #{onlineId}
and dor.inspector_type_id in
#{typeId}
GROUP BY dor.post_id,dor.candidate_id) dor1 LEFT JOIN (SELECT doil.online_id,SUM(doil.pub_count) AS pub_counts,SUM(doil.finish_count)AS finish_counts FROM dr_online_inspector_log doil GROUP BY doil.online_id) doil1 ON doil1.online_id=dor1.online_id LEFT JOIN dr_online_candidate doc ON doc.id=dor1.candidate_id ORDER BY dor1.post_id desc
