mapper.select:
select user_id, is_from_stage, realname, sum(c.agree) as agree, sum(c.degree) as degree,
sum(c.abstain) as abstain,sum(c.invalid) as invalid from pcs_vote_candidate c, pcs_vote_group g
 where g.id=c.group_id and g.has_report=1 and g.type=#{type}

    and c.user_id=#{userId}


    and c.is_from_stage=#{isFromStage}
 group by c.user_id  order  by  agree desc, c.sort_order asc, c.user_id asc

mapper.sql:
where c.user_id= u.id and c.config_id = #{configId} and c.stage = #{stage}
and is_chosen =1 and is_proposal=1

    and (u.username like #{search} or u.realname like #{search} or u.code like #{search})

mapper.select:
select sum(if(type=1, 1, 0)) pro_count, sum(if(type=2, 1, 0)) stu_count,
sum(if(type=3, 1, 0)) retire_count, sum(if(gender=2, 1, 0)) female_count,
sum(if(nation not like '%汉%', 1,0)) minority_count,
sum(if( TIMESTAMPDIFF(YEAR, birth, now()) < 50, 1, 0)) under_fifty_count
from pcs_pr_candidate where config_id=#{configId} and stage=#{stage}

    and party_id=#{partyId}


    and is_chosen=#{isChosen}

sql.where:
pp.config_id=#{configId}

    and pp.party_id=#{partyId}


    and ppr.has_report=1


    and (ppr.id is null or ppr.has_report=0)


    and ppr.status=#{recommendStatus}

mapper.select:
select pp.*, if(ppr.id is null or ppr.has_report=0, pp.branch_count, ppr.branch_count) as real_branch_count,
     if(ppr.id is null or ppr.has_report=0, pp.member_count, ppr.member_count) as real_member_count,
     if(ppr.id is null or ppr.has_report=0, pp.positive_count, ppr.positive_count) as real_positive_count,
     if(ppr.id is null or ppr.has_report=0, pp.student_member_count, ppr.student_member_count) as real_student_member_count,
     if(ppr.id is null or ppr.has_report=0, pp.teacher_member_count, ppr.teacher_member_count) as real_teacher_member_count,
     if(ppr.id is null or ppr.has_report=0, pp.retire_member_count, ppr.retire_member_count) as real_retire_member_count,
       ${stage} as stage, pp.config_id,
ppr.expect_member_count,ppr.expect_positive_member_count,
ppr.actual_member_count, ppr.actual_positive_member_count, ppr.has_report,
ppr.status as recommend_status, ppr.check_remark from pcs_party pp
left join pcs_pr_recommend ppr on ppr.config_id=pp.config_id and ppr.stage=#{stage} and ppr.party_id = pp.party_id

order by pp.sort_order desc

select ppa.id, op.party_id, op.name as party_name, op.member_count, op.positive_count, op.config_id, ppa.candidate_count, ppa.pro_count, ppa.stu_count, ppa.retire_count, ppa.female_count, ppa.minority_count, ppa.under_fifty_count from pcs_party op left join pcs_pr_allocate ppa on op.config_id=ppa.config_id and op.party_id=ppa.party_id where op.config_id=#{configId}
and op.id=${partyId}
order by op.sort_order desc

select count(*) from pcs_party op
where op.config_id=#{configId}

    and op.id=${partyId}

sql.where:
pp.config_id=#{configId}
    -- 未报送时读取参与党代会的分党委，已报送读取报送的
    and ((par.id is null and pp.is_deleted = 0) or par.id>0)

    and pp.party_id=#{partyId}



        and par.id is not null


        and par.id is null

pb.config_id = #{configId}
-- 未报送时读取参与党代会的支部，已报送读取报送的支部
and ((par.id is null and pb.is_deleted = 0) or (par.id>0 and pr.id>0))

    and pb.party_id=#{partyId}


    and pb.branch_id=#{branchId}


    and pb.branch_id in

        #{branchId}



    and pr.is_finished=#{isFinished}

mapper.select:
select pc.branch_id
from pcs_candidate pc
  where pc.config_id=#{configId} and pc.stage=#{stage} and pc.type=#{candidateType}
    and pc.branch_id is not null and user_id=#{userId}
    and pc.party_id in (select party_id from pcs_admin_report where config_id=#{configId} and stage=#{stage})

sql.where:

    and pc.user_id=#{userId}

and pc.config_id=#{configId} and pc.stage=#{stage} and pc.type=#{candidateType} and
pc.party_id=#{partyId}

mapper.select:
select count(distinct user_id) from
 (
 select candidate_user_id as user_id from pcs_poll_result where poll_id=#{pollId} and type=#{type}

     and candidate_user_id = #{userId}

 union all
 select user_id from pcs_poll_result   and status=2 and user_id is not null) tmp

mapper.sql:
r.stage=#{stage} and r.config_id=#{configId} and pp.has_report=1 and pp.is_deleted=0

    and r.party_id = #{partyId}


    and r.type = #{type}


    and r.branch_id = #{branchId}


    and r.user_id in

        #{userId}



    and (r.party_id in

        #{partyId}

    or r.branch_id in

        #{branchId})



    and r.branch_id in

        #{branchId}



    and r.party_id in

