mapper.delete:
delete pmp.* from pmd_member_pay pmp, pmd_member pm where pmp.member_id=pm.id
and pm.month_id=#{currentMonthId} and pm.party_id = #{partyId} and pm.branch_id = #{branchId} and pm.has_pay=0;
delete from pmd_member where month_id=#{currentMonthId} and party_id = #{partyId} and branch_id = #{branchId} and has_pay=0;

mapper.select:
select count(distinct pmpv.user_id) from pmd_member_pay_view pmpv,
(select * from pmd_member where month_id=#{currentMonthId}

    and party_id=#{partyId}


    and branch_id=#{branchId}
) pm
where pmpv.user_id=pm.user_id and pmpv.is_delay=1 and pmpv.has_pay=0

select sum(pmpv.due_pay) from pmd_member_pay_view pmpv,
(select * from pmd_member where month_id=#{currentMonthId}

    and party_id=#{partyId}


    and branch_id=#{branchId}
) pm
where pmpv.user_id=pm.user_id and pmpv.is_delay=1 and pmpv.has_pay=0

mapper.sql:
from pmd_member_pay_view pmpv
where pmpv.is_delay=1 and ((pmpv.has_pay=1 and pay_month_id=#{currentMonthId}) or pmpv.has_pay=0)


    and pmpv.party_id=#{partyId}


    and pmpv.branch_id=#{branchId}


    and pmpv.has_pay=#{hasPay}


    and pmpv.user_id=#{userId}

 and pmpv.month_id < #{currentMonthId}

from pmd_member_pay_view pmpv,
(select * from pmd_member where month_id=#{monthId}

    and party_id=#{partyId}


    and branch_id=#{branchId}
) pm
where pmpv.user_id=pm.user_id and pmpv.is_delay=1 and pmpv.has_pay=1

    and pmpv.user_id=#{userId}

and pmpv.pay_month_id = #{monthId}

mapper.select:
select count(*) as total,
sum(if(type=1 and authorized_type='事业编' and post_class='专业技术岗位', 1, 0)) as zj, sum(if(type=1 and authorized_type='事业编' and post_class='管理岗位', 1, 0)) as gl, sum(if(type=1 and authorized_type='事业编' and post_class='工勤技能岗位', 1, 0)) as gq, sum(if(type=1 and authorized_type<>'事业编' and staff_type='校聘', 1, 0)) as xp, sum(if(type=1 and authorized_type<>'事业编' and staff_type='学生助理', 1, 0)) as xszl,
-- 离退教职工 sum(if(type=2, 1, 0)) as lt, -- 不带薪学生党员 sum(if(type=3 and has_salary=0, 1, 0)) as bdx, -- 带薪学生党员 sum(if(type=3 and has_salary=1, 1, 0)) as dx from pmd_member pm where month_id=#{monthId} and party_id=#{partyId}
-- 本月按时缴纳党费党员数 and is_delay=0 and has_pay=1
-- 本月未缴纳党费党员数 and has_pay=0
-- 补缴往月党费党员数 and exists(select 1 from pmd_member_pay_view where user_id=pm.user_id and has_pay=1 and pay_month_id=#{monthId} and month_id<#{monthId} and charge_party_id=#{partyId});

select if(f.user_type in(2,3,4), 1, if(f.user_type=1, 2, 3)) as userType, count(distinct f.user_id) as num, sum(f.amt) as amt from pmd_fee f, sys_user u
where f.user_id=u.id and f.has_pay=1 and date_format(f.pay_time, '%Y.%m')=#{payMonth}

    and party_id=#{partyId}

group by userType
